pragma solidity >=0.4.0 <0.6.0;

import 'openzeppelin-solidity/contracts/ownership/Ownable.sol';

contract Notary is Ownable {

  event Notarize(bytes32 indexed hash);

  mapping(bytes32 => bool) private _notary;

  function notarize(bytes32 hash) public onlyOwner {
    _notary[hash] = true;

    emit Notarize(hash);
  }

  function isNotarized(bytes32 hash) public view returns(bool) {
    return _notary[hash];
  }

}
